% ==============================
% Espejo de Bragg - Reflectancia
% ==============================

clear; clc;

% Longitud de onda central (normalizada)
lambda0 = 1;

% Índices alto, bajo, incidente y sustrato
nHnL = [4, 1.5];
n_inc = 1;
n_sub = 1.5; % Típicamente el sustrato es vidrio o el mismo material nL

% Número de pares
m = 20;

% Vector de índices: Incidente + 2m capas + Sustrato
n = [n_inc, repmat(nHnL, 1, m), n_sub];

% Espesores λ/4 ajustados a lambda0 (Solo las 2m capas intermedias)
d1 = lambda0./(4.*nHnL);
d = repmat(d1, 1, m);

% Ángulo de incidencia normal
theta_i0 = 0;   

% Barrido en longitud de onda
lambda = linspace(0.5*lambda0, 1.5*lambda0, 1000);
R = zeros(size(lambda));

for k = 1:length(lambda)
    [~,~,~,~,~,r,~] = MultInterference(1, theta_i0, n, d, lambda(k), false);
    
    % Reflectancia de Potencia = |r|^2 (a incidencia normal)
    R(k) = abs(r).^2;
end

% Valores teóricos
% Ancho de banda normalizado (en frecuencia / número de onda g)
BW_freq = 4*asin((nHnL(1)-nHnL(2))/sum(nHnL))/pi; 

% Limites de longitud de onda teóricos (inversamente proporcionales)
lambda_BW_min = 1 / (1 + BW_freq/2); 
lambda_BW_max = 1 / (1 - BW_freq/2); 

% Rmax teórico considerando el sustrato
ratio_indice = n_sub * (nHnL(1)/nHnL(2))^(2*m);
Rmax = ( (n_inc - ratio_indice) / (n_inc + ratio_indice) )^2;

% Graficar
figure; %[output:1486aa77]
plot(lambda/lambda0, R, 'LineWidth', 2); %[output:1486aa77]
hold on; %[output:1486aa77]

% Línea horizontal de Rmax
yline(Rmax, 'r--', 'LineWidth', 1.5); %[output:1486aa77]

% Líneas verticales del ancho de banda (Teórico)
xline(lambda_BW_min, 'g--', 'LineWidth', 1.5); %[output:1486aa77]
xline(lambda_BW_max, 'g--', 'LineWidth', 1.5); %[output:1486aa77]

xlabel('\lambda / \lambda_0'); %[output:1486aa77]
ylabel('Reflectancia R'); %[output:1486aa77]
title('Reflectancia - Espejo de Bragg'); %[output:1486aa77]
grid on; %[output:1486aa77]
legend('R simulada', 'R_{max} Teórico', 'BW Teórico', 'Location', 'northoutside', 'Orientation', 'horizontal'); %[output:1486aa77]

% Ajustar los ejes para margen extra
xMargin = 0.05 * (max(lambda/lambda0) - min(lambda/lambda0));
yMargin = 0.1;
axis([min(lambda/lambda0)-xMargin, max(lambda/lambda0)+xMargin, -yMargin, 1+yMargin]); %[output:1486aa77]

hold off; %[output:1486aa77]

